mapper.update:
UPDATE board
SET boardViews = NVL(boardViews, 0) + 1
WHERE boardNumber = #{boardNumber}

mapper.select:
SELECT COUNT(*) FROM
board_likes
WHERE boardNumber = #{boardNumber}
AND userNumber =
#{userNumber}

mapper.update:
UPDATE board
SET boardLikes = NVL(boardLikes, 0) + 1
WHERE boardNumber = #{boardNumber}

mapper.select:
SELECT boardLikes FROM
board
WHERE boardNumber = #{boardNumber}
